import { useState } from "react";

const Search = () => {

    const {data:searchresult, isPending, error, setUrl} = useFecth(url);

    const [searchformovie,setSearchForMovie]=useState('')

    const handlechange = (event)=>{
      setSearchForMovie(event.target.value);
    }

    const handleSearch = ()=>{

      const url = 'https://api.themoviedb.org/3/search/movie?api_key=05b670c12d1973fe82919a005ef06481&query='+searchformovie;

      setUrl(url);
    }

    return (
        <a className="field has-addons">
      <p className="control">
        <input className="input" value={searchformovie} onChange={handlechange} type="text" placeholder="search for movie"/>
      </p>
      <p className="control">
        <button className="button" onClick={handleSearch}>
        <span><i className="fa-solid fa-magnifying-glass"></i></span>
        </button>
      </p>
    </a>
    );
}

export default Search;
