import { useEffect, useState } from "react";
import Carousel from "react-elastic-carousel";
import useFecth from "./usefectch";

const Genre = ({genre}) =>{

    const {data:res, isPending, error, setData, setUrl} = useFecth('https://api.themoviedb.org/3/discover/movie?api_key=05b670c12d1973fe82919a005ef06481&with_genres='+genre.id);

    const {data:language} = useFecth('https://api.themoviedb.org/3/configuration/languages?api_key=05b670c12d1973fe82919a005ef06481');

    const [movies,setMovies]=useState(null);

    useEffect(()=>{
        if(res!=null)setMovies(res.results);
    },[res])

    const base_url = 'https://image.tmdb.org/t/p/w500';

    const handleRequest = (order)=>{

        if(order==1){
            const copy = [...movies];
            copy.sort((a,b)=>a.popularity-b.popularity);
            setMovies(copy);
        }

        if(order==2){
            const copy = [...movies];
            copy.sort((a,b)=>b.popularity-a.popularity);
            setMovies(copy); 
        }

        if(order==3){
            const copy = [...movies];
            copy.sort((a,b)=>new Date(a.release_date)-new Date(b.release_date));
            setMovies(copy);console.log(movies);
        }

        if(order==4){
            const copy = [...movies];
            copy.sort((a,b)=>new Date(b.release_date)-new Date(a.release_date));
            setMovies(copy);console.log(movies);
        }

        if(order==5){
            const copy = [...movies];
            copy.sort((a,b)=>a.vote_average-b.vote_average);
            setMovies(copy);
        }

        if(order==6){
            const copy = [...movies];
            copy.sort((a,b)=>b.vote_average-a.vote_average);
            setMovies(copy);
        }

        if(order==7){
            const copy = [...movies];
            copy.sort((a,b)=>a.vote_count-b.vote_count);
            setMovies(copy);
        }

        if(order==8){
            const copy = [...movies];
            copy.sort((a,b)=>b.vote_count-a.vote_count);
            setMovies(copy);
        }
    }

    const breakPoints = [
        { width: 1, itemsToShow: 1, itemsToScroll:1},
        { width: 550, itemsToShow: 2, itemsToScroll:2},
        { width: 850, itemsToShow: 3, itemsToScroll:3},
        { width: 1150, itemsToShow: 4, itemsToScroll:4},
        { width: 1450, itemsToShow: 5, itemsToScroll:5},
        { width: 1750, itemsToShow: 6, itemsToScroll:6},
      ]

      const [searchforlanguage,setSearchForLanguage]=useState('');

    const handlechange = (event)=>{
      setSearchForLanguage(event.target.value);
    }

    const handleSearch = ()=>{console.log(language);

        const ind = language.find(element=>element.english_name==searchforlanguage);

        console.log(ind);

        if(ind)
        setUrl('https://api.themoviedb.org/3/discover/movie?api_key=05b670c12d1973fe82919a005ef06481&with_original_language='+language[ind].english_name);

    }

    return(
<div className="container py-6">
<div className="box has-shadow">
<nav className="navbar is-white">
            <div className="navbar-brand">
                <a className="navbar-item">
                <div className="heading">
                 {genre.name}
                 </div>
                </a>
            </div>
            <div className="navbar-menu">
                <div className="navbar-start">
                    <div className="navbar-item is-hoverable has-dropdown">
                        <a className="navbar-link">Sort By</a>
                        <div className="navbar-dropdown">
                            <a onClick={()=>{handleRequest(1)}} className="navbar-item">popular: low to high</a>
                            <a onClick={()=>{handleRequest(2)}} className="navbar-item">popular: high to low</a>
                            <a onClick={()=>{handleRequest(3)}} className="navbar-item">release date: low to high</a>
                            <a onClick={()=>{handleRequest(4)}} className="navbar-item">release date: high to low</a>
                            <a onClick={()=>{handleRequest(5)}} className="navbar-item">rating: low to high</a>
                            <a onClick={()=>{handleRequest(6)}} className="navbar-item">rating: high to low</a>
                            <a onClick={()=>{handleRequest(7)}} className="navbar-item">no of votes: low to high</a>
                            <a onClick={()=>{handleRequest(8)}} className="navbar-item">no of votes: high to low</a>
                        </div>
                    </div>
                </div>
                <div className="navbar-end">
                    <div className="navbar-item">
                    <a className="field has-addons">
                    <p className="control">
                    <input className="input" value={searchforlanguage} onChange={handlechange} type="text" placeholder="search by language"/>
                    </p>
                    <p className="control">
                    <button className="button" onClick={()=>handleSearch()}>
                    <span><i className="fa-solid fa-magnifying-glass"></i></span>
                    </button>
                    </p>
                    </a>
                    </div>
                </div>
            </div>
        </nav>
<Carousel itemPadding={[10,10]} breakPoints={breakPoints}>
{movies && movies.map((movie)=>
    <div className="card">
<div className="card-image">
    <figure className="image">
        <img src={base_url+movie.poster_path}/>
    </figure>
</div>
<div className="card-content">
    <div className="media">
        <div className="media-left">
            <button className="button is-dark has-text-weight-bold has-text-justified">{movie.vote_average}</button>
        </div>
        <div className="media-content">
            <h3 className="has-text-weight-bold">{movie.title}</h3>
        </div>
    </div>
    <div className="columns">
        <div className="column">
            <span className="icon-text">
                <span className="icon"><i class="fa-solid fa-hashtag"></i></span>
                <span>{movie.vote_count/1000+"k"}</span>
            </span>
        </div>
        <div className="column">
            <span className="icon-text">
                <span className="icon"><i class="fa-solid fa-clock"></i></span>
                <span>{new Date(movie.release_date).toDateString().slice(11,15)}</span>
            </span>
        </div>

    </div>

</div>
</div>
)}
</Carousel>
<div className="level-right">
<div className="level-item">
<button className="button">{">>"}</button>
</div>
</div>
</div>
</div>
    )
}

export default Genre;
