import useFecth from "./usefectch";

const Genre = () => {

    const {data:genres, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/genre/movie/list?api_key=05b670c12d1973fe82919a005ef06481');

    let dataAll = null;

    useEffect(()=>{
        if(genres!=null){

               for(let i=0;i<genres.length;i++){

                let url = 'https://api.themoviedb.org/3/discover/movie?api_key=05b670c12d1973fe82919a005ef06481&with_genres='+genre.id;

                const {data, isPending, error, setUrl} = useFecth(url);

                useEffect(()=>{if(data!=null){dataAll.concat({data:data,name:genre.name});}});
               }
        }
    },[genres])



    return (
        <div className=""></div> 
    );
}

export default Genre;
