import { useState } from "react";
import useFecth from "./usefectch";

const Popularity = () => {

   const url = 'https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=05b670c12d1973fe82919a005ef06481';

   const {data:populars, isPending, error} = useFecth(url);

   const base_url = 'https://image.tmdb.org/t/p/w500';

   const Isvalid = ({populars})=>{

    const [makevisible,setMakeVisible]=useState(false);

    const toggler_func = (event) =>{

        const toggler_element = document.getElementById('toggler-tag');

        console.log('success');

        toggler_element.classList.remove('is-hidden');
    }

    const list = populars.map((popular)=>
        <div className="column is-4">
            <div className="card">
            <div className="card-image">
                <figure className="image ">
                    <img src={base_url+popular.poster_path}/>
                </figure>
            </div>
            <div className="card-content">
                <div className="media">
                    <div className="media-left">
                        <button className="button is-dark has-text-weight-bold">{popular.vote_average}</button>
                    </div>
                    <div className="media-content">
                        <h3 className="has-text-weight-bold">{popular.title}</h3>
                    </div>
                </div>
            </div>
            <div className="box">
                <div className="content">
                    <p>{popular.overview.slice(0,popular.overview.length/2)}<a onclick={(event)=>toggler_func(event)}>...</a></p>
                    <p id = "toggler-tag"className="is-hidden">{popular.overview.slice(popular.overview.length/2)}</p>
                </div>
            </div>
        </div>
        </div>
    )

    return (
        <div className="columns is-multiline">{list}</div>
    )
}

return (
    <div>{populars && <Isvalid populars = {populars}/>}</div>
)

}

export default Popularity;
