import CarouselDesign from "./carousel_design";
import useFecth from "./usefectch";

const Trending = () => {

    const {data:moviesTrending}=useFecth('https://api.themoviedb.org/3/trending/movie/day?api_key=05b670c12d1973fe82919a005ef06481');
    const {data:tvShowsTrending}=useFecth('https://api.themoviedb.org/3/trending/tv/day?api_key=05b670c12d1973fe82919a005ef06481');
    return (
        <>
        {moviesTrending && <CarouselDesign data={moviesTrending.results}/>}
        {tvShowsTrending && <CarouselDesign data={tvShowsTrending.results}/>}
        </>
    );
}

export default Trending;
