import { useParams } from "react-router-dom";
import useFecth from "./usefectch";
const Search = () => {

    const {name} = useParams();

    const {data:searchResult, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/search/movie?api_key=05b670c12d1973fe82919a005ef06481&query='+name)

   const base_url = 'https://image.tmdb.org/t/p/w500';

    return (  
        <div>{searchResult && searchResult.filter(element => element.poster_path!=null && element.title!=null && element.overview!=null).map((res)=>
            <div className="container py-5">
                <div className="box">
                <article className="media">
             <figure className="media-left">
                <div className="card">
                    <div className="card-image">
                    <p className="image is-128x128">
                    <img src={base_url + res.poster_path}/>
                </p>
                    </div>
                </div>
            </figure>
            <div className="media-content">
                <div className="content">
                    <p><strong>{res.title}</strong></p>
                    <br />
                    <h3>{res.overview}</h3>
                </div>
            </div>
        </article>
                </div>
            </div>
        )}</div>
    );
}

export default Search;
