import CarouselDesign from "./carousel_design";
import useFecth from "./usefectch";

const Genre = ({genre}) =>{

    const {data:eachGenreResult, setData, setUrl} = useFecth('https://api.themoviedb.org/3/discover/movie?api_key=05b670c12d1973fe82919a005ef06481&with_genres='+genre.id);

    const {data:language} = useFecth('https://api.themoviedb.org/3/configuration/languages?api_key=05b670c12d1973fe82919a005ef06481');

    return(
        <>
        {eachGenreResult && language && <CarouselDesign data={eachGenreResult.results} searchData={language} setData={setData} extras={genre} setUrl={setUrl}/>}
        </>
    )
}

export default Genre;
