import Genre from "./genre";
import useFecth from "./usefectch";
import { NavLink, Outlet, useParams, Link } from "react-router-dom";
import { useEffect } from "react";

const Genres = () => {

    const {extra_link} = useParams();

    const {data:genres, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/genre/'+extra_link+'/list?api_key=05b670c12d1973fe82919a005ef06481');

    useEffect(()=>{
        console.log(genres);
        console.log(extra_link);
    },[genres])

    return (
        <>
        <nav className="navbar is-white">
            <div className="navbar-menu">
                <div className="navbar-start my-3">
                        <div className="navbar-item">
                        <Link >MOVIES</Link>
                        </div>
                        <div className="navbar-item">
                        <Link to="/genre/tv">TVSHOWS</Link>
                        </div>
                </div>
            </div>
        </nav>{extra_link}
        {genres && genres.genres.map((genre)=>
        <Genre genre={genre} extra_link={extra_link}/>
        )}
        </> 
    );
}

export default Genres;
