import { useParams } from "react-router-dom";
import useFecth from "./usefectch";
const Search = () => {

    const {name} = useParams();

    const {data:searchResult, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/search/movie?api_key=05b670c12d1973fe82919a005ef06481&query='+name)

   const base_url = 'https://image.tmdb.org/t/p/w500';

    return (  
        <div>{searchResult && searchResult.filter(element => element.poster_path!=null && element.title!=null && element.overview!=null).map((res)=>
            <div className="container py-5">
                <div className="box has-shadow">
                <div className="media">
                    <div className="media-left">
                    <div className="card">
                        <div className="card-image">
                        <div className="image">
                            <img src={base_url+res.poster_path}/>
                        </div>
                        </div>
                    </div>
                    </div>
                    <div className="media-content">
                        <div className="content">
                            <h6 className="has-text-justified">{res.title}</h6>
                            <span class="icon-text">
  <span class="icon">
    <i class="fas fa-home"></i>
  </span>
  <span>Home</span>
</span>
                            <blockquote className="heading">{res.overview}</blockquote>
                        </div>
                    </div>
                </div>
                </div>
            </div>
        )}</div>
    );
}

export default Search;
