import { useState } from "react";
import Carousel from "react-elastic-carousel";
import useFecth from "./usefectch";

const Genre = ({genre}) =>{

    const {data:movies, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/discover/movie?api_key=05b670c12d1973fe82919a005ef06481&with_genres='+genre.id);

    const base_url = 'https://image.tmdb.org/t/p/w500';

    const breakPoints = [
        { width: 1, itemsToShow: 1, itemsToScroll:1},
        { width: 550, itemsToShow: 2, itemsToScroll:2},
        { width: 850, itemsToShow: 3, itemsToScroll:3},
        { width: 1150, itemsToShow: 4, itemsToScroll:4},
        { width: 1450, itemsToShow: 5, itemsToScroll:5},
        { width: 1750, itemsToShow: 6, itemsToScroll:6},
      ]

      const [searchformovie,setSearchForMovie]=useState('');

    const handlechange = (event)=>{
      setSearchForMovie(event.target.value);
    }

    const handleSearch = ()=>{
      window.location.href = '/search/'+searchformovie;
    }

    return(
        <div className="container py-6">
            <div className="box has-shadow">
        <div className="heading">
            {genre.name}
        </div>
        <a className="field has-addons">
      <p className="control">
        <input className="input" value={searchformovie} onChange={handlechange} type="text" placeholder="search for movie"/>
      </p>
      <p className="control">
        <button className="button" onClick={()=>handleSearch()}>
        <span><i className="fa-solid fa-magnifying-glass"></i></span>
        </button>
      </p>
    </a>
            <Carousel itemPadding={[10,10]} breakPoints={breakPoints}>
            {movies && movies.results.map((movie)=>
                <div className="card">
            <div className="card-image">
                <figure className="image">
                    <img src={base_url+movie.poster_path}/>
                </figure>
            </div>
            <div className="card-content">
                <div className="media">
                    <div className="media-left">
                        <button className="button is-dark has-text-weight-bold has-text-justified">{movie.vote_average}</button>
                    </div>
                    <div className="media-content">
                        <h3 className="has-text-weight-bold">{movie.title}</h3>
                    </div>
                </div>
            </div>
        </div>
            )}
            </Carousel>
        <div className="level-right">
            <div className="level-item">
            <button className="button">{">>"}</button>
            </div>
        </div>
        </div>
        </div>
    )
}

export default Genre;
