import Genre from "./genre";
import useFecth from "./usefectch";
import { NavLink } from "react-router-dom";

const Genres = () => {

    const {data:genres, isPending, error, setUrl} = useFecth('https://api.themoviedb.org/3/genre/movie/list?api_key=05b670c12d1973fe82919a005ef06481');

    return (
        <>
        <nav className="navbar is-white">
            <div className="navbar-menu">
                <div className="navbar-start my-3">
                        <NavLink style={()=>({textDecoration:'none'})} className={({isActive})=>(isActive?'navbar-item has-text-weight-bold tag is-light is-large mx-2':'navbar-item has-text-weight-bold mx-2')} to="">MOVIES</NavLink>
                        <NavLink className={({isActive})=>(isActive?'navbar-item has-text-weight-bold tag is-light is-large mx-2':'navbar-item has-text-weight-bold mx-2')} to="tvshows">TVSHOWS</NavLink>
                </div>
            </div>
        </nav>
        {genres && genres.genres.map((genre)=>
        <Genre genre={genre}/>
        )}
        </> 
    );
}

export default Genres;
