import { useState } from "react";
import useFecth from "./usefectch";

const Popularity = () => {

   const url = 'https://api.themoviedb.org/3/discover/movie?sort_by=popularity.desc&api_key=05b670c12d1973fe82919a005ef06481';

   const {data:populars, isPending, error} = useFecth(url);

   const base_url = 'https://image.tmdb.org/t/p/w500';

   const Isvalid = ({populars})=>{

    const list = populars.map((popular)=>{
        <div className="card">
            <div className="card-image">
                <figure className="image">
                    <img src={base_url+popular.poster_path}/>
                </figure>
            </div>
        </div>
    })

    return list;
}

return (
    <div>{populars && <Isvalid populars = {populars}/>}</div>
)

}

export default Popularity;
